def main(transport='stdio', host='127.0.0.1', port=3434):
    """
    Main entry point for the MCP server.

    Args:
        transport: Transport type ('stdio' or 'sse')
        host: Host to bind to when using SSE transport
        port: Port to use when using SSE transport
    """
    # Initialize the TickTick client
    if not initialize_client():
        logger.error("Failed to initialize TickTick client. Please check your API credentials.")
        return

    # Run the server with the specified transport
    if transport == 'sse':
        print(f"Starting TickTick MCP server with SSE transport on {host}:{port}")

        # Configure the SSE transport endpoint to match what clients expect
        SseServerTransport.ENDPOINT = "/sse"

        # Create an SSE transport
        sse = SseServerTransport("/messages/")

        # Define SSE handler
        async def handle_sse(request):
            async with sse.connect_sse(
                request.scope, request.receive, request._send
            ) as streams:
                await mcp.run(streams[0], streams[1], mcp.create_initialization_options())
            return Response()

        # Create Starlette app with routes
        from starlette.applications import Starlette
        from starlette.responses import Response
        from starlette.routing import Mount, Route

        starlette_app = Starlette(
            debug=True,
            routes=[
                Route("/sse", endpoint=handle_sse, methods=["GET"]),
                Mount("/messages/", app=sse.handle_post_message),
            ]
        )

        # Print connection info
        print(f"\n========= CONNECTION INFO =========")
        print(f"Connect to this server at:")
        print(f"URL: http://{host}:{port}")
        print(f"SSE endpoint: {SseServerTransport.ENDPOINT}")
        print(f"=======================================\n")

        # Run the Starlette app with Uvicorn
        uvicorn.run(
            starlette_app, 
            host=host, 
            port=port, 
            log_level="info"
        )
    else:
        print("Starting TickTick MCP server with stdio transport")
        # For stdio, we need to run in an async context
        import asyncio
        import anyio
        from mcp.server.stdio import stdio_server

        # Set up specific stdio logging
        stdio_logger = logging.getLogger("mcp.stdio")
        stdio_logger.setLevel(logging.DEBUG)

        # Create initialization options
        initialization_options = mcp.create_initialization_options()
        logger.debug(f"Initialization options: {initialization_options}")

        async def arun():
            try:
                async with stdio_server() as streams:
                    logger.info("Stdio server started, streams established")
                    await mcp.run(
                        streams[0], streams[1], initialization_options
                    )
            except Exception as e:
                logger.error(f"Exception in stdio_server context: {e}", exc_info=True)
                raise

        try:
            logger.info("Starting anyio runtime with stdio transport")
            anyio.run(arun)
        except Exception as e:
            logger.error(f"Error running stdio server: {e}", exc_info=True)

if __name__ == "__main__":
    main()

