#!/usr/bin/env python3
"""Analyze topics in markdown files to understand content structure."""

from pathlib import Path
import re
from collections import defaultdict, Counter
import json


def extract_topic_from_filename(filename):
    """Extract and normalize topic from filename."""
    # Remove .md extension
    name = filename.replace('.md', '')

    # Common patterns to normalize
    replacements = {
        '_': ' ',
        'Meal ': 'Meals ',
        'Med insurance': 'Medical insurance',
        'Time off': 'Time Off',
        'Ð¡ountry specific': 'Country specific',
        'Life & Accident': 'Life and Accident',
        'Life & accidental': 'Life and Accident',
    }

    for old, new in replacements.items():
        name = name.replace(old, new)

    return name.strip()


def analyze_markdown_files():
    """Analyze all markdown files and extract topics."""
    data_dir = Path("/Users/egv/dev/yandex/dino-bot/data")

    # Structure to hold analysis
    analysis = {
        'regions': defaultdict(lambda: {
            'countries': defaultdict(lambda: {
                'files': [],
                'topics': []
            })
        }),
        'topics': Counter(),
        'topic_categories': defaultdict(set)
    }

    # Process all markdown files
    for md_file in data_dir.rglob("*.md"):
        path_parts = md_file.relative_to(data_dir).parts

        if len(path_parts) >= 1:
            region = path_parts[0]

            # Determine country and topic
            if len(path_parts) == 2:
                # Direct region file (e.g., Africa/Ghana.md)
                country = extract_topic_from_filename(path_parts[1])
                topic = "Country Overview"
            elif len(path_parts) >= 3:
                # Country subdirectory file
                country = path_parts[1]
                topic = extract_topic_from_filename(path_parts[-1])
            else:
                continue

            # Store the analysis
            analysis['regions'][region]['countries'][country]['files'].append(str(md_file.relative_to(data_dir)))
            analysis['regions'][region]['countries'][country]['topics'].append(topic)
            analysis['topics'][topic] += 1

            # Categorize topics
            topic_lower = topic.lower()
            if any(word in topic_lower for word in ['insurance', 'medical', 'health', 'accident']):
                analysis['topic_categories']['Insurance & Healthcare'].add(topic)
            elif any(word in topic_lower for word in ['payroll', 'salary', 'tax', 'kpi', 'bonus', 'financial support', 'allowance']):
                analysis['topic_categories']['Compensation & Finance'].add(topic)
            elif any(word in topic_lower for word in ['time off', 'vacation', 'leave', 'sick', 'maternity']):
                analysis['topic_categories']['Time Off & Leave'].add(topic)
            elif any(word in topic_lower for word in ['office', 'access', 'parking', 'printer', 'wifi', 'smoking', 'fire safety', 'lockers']):
                analysis['topic_categories']['Office & Facilities'].add(topic)
            elif any(word in topic_lower for word in ['meal', 'food', 'eat', 'discount', 'sports']):
                analysis['topic_categories']['Benefits & Perks'].add(topic)
            elif any(word in topic_lower for word in ['travel', 'business trip', 'visa']):
                analysis['topic_categories']['Travel & Immigration'].add(topic)
            elif any(word in topic_lower for word in ['offboarding', 'resignation', 'termination']):
                analysis['topic_categories']['Offboarding'].add(topic)
            elif any(word in topic_lower for word in ['compliance', 'ethics', 'policy', 'regulation', 'safety']):
                analysis['topic_categories']['Compliance & Policies'].add(topic)
            elif any(word in topic_lower for word in ['psychological', 'mental', 'telus', 'yasno', 'zigmund', 'openup']):
                analysis['topic_categories']['Mental Health & Support'].add(topic)
            elif any(word in topic_lower for word in ['onboarding', 'welcome', 'employee handbook', 'directory']):
                analysis['topic_categories']['Onboarding & HR'].add(topic)
            else:
                analysis['topic_categories']['Other'].add(topic)

    return analysis


def print_analysis(analysis):
    """Print the analysis results."""
    print("=" * 80)
    print("MARKDOWN FILES ANALYSIS")
    print("=" * 80)

    # Print regions and countries
    print("\n## REGIONAL STRUCTURE")
    print("-" * 40)
    for region, region_data in sorted(analysis['regions'].items()):
        print(f"\n### {region}")
        for country, country_data in sorted(region_data['countries'].items()):
            print(f"  - {country}: {len(country_data['files'])} files")

    # Print topic categories
    print("\n## TOPIC CATEGORIES")
    print("-" * 40)
    for category, topics in sorted(analysis['topic_categories'].items()):
        print(f"\n### {category}")
        for topic in sorted(topics):
            count = analysis['topics'][topic]
            print(f"  - {topic} ({count} files)")

    # Print most common topics
    print("\n## MOST COMMON TOPICS")
    print("-" * 40)
    for topic, count in analysis['topics'].most_common(20):
        print(f"  {count:3d} - {topic}")

    # Save detailed analysis to JSON
    # Convert sets to lists for JSON serialization
    json_analysis = {
        'regions': dict(analysis['regions']),
        'topics': dict(analysis['topics']),
        'topic_categories': {k: list(v) for k, v in analysis['topic_categories'].items()}
    }

    with open('topics_analysis.json', 'w', encoding='utf-8') as f:
        json.dump(json_analysis, f, indent=2, ensure_ascii=False)

    print("\nâœ“ Detailed analysis saved to topics_analysis.json")


def main():
    """Main entry point."""
    analysis = analyze_markdown_files()
    print_analysis(analysis)


if __name__ == "__main__":
    main()

