def _format_events_as_json(self, events: List[EventNotification]) -> str:
    """Format events as JSON."""
    notifications_data = []

    for notification in events:
        # Convert the entire notification to dict using Pydantic's model_dump
        notif_dict = notification.model_dump(mode='json')

        # Restructure to match expected format
        formatted_notif = {
            "event": notif_dict.get("event", {}),
            "classification": None,
            "notifications": notif_dict.get("notifications", [])
        }

        # Add classification if present
        if "event" in notif_dict and notif_dict["event"]:
            event_data = notif_dict["event"]
            if "size" in event_data or "importance" in event_data or "target_audiences" in event_data:
                formatted_notif["classification"] = {
                    "size": event_data.get("size"),
                    "importance": event_data.get("importance"),
                    "target_audiences": event_data.get("target_audiences", [])
                }
                # Remove classification fields from event
                for field in ["size", "importance", "target_audiences"]:
                    event_data.pop(field, None)

        notifications_data.append(formatted_notif)

    import json
    return json.dumps({"notifications": notifications_data}, indent=2)

