def _format_events_as_json(self, events: List[EventNotification]) -> str:
    """Format events as JSON with full notification data."""
    notifications_data = []
    for notification in events:
        # The notification.event is a ClassifiedEvent which contains both event and classification
        classified_event = notification.event
        base_event = classified_event.event if hasattr(classified_event, 'event') else classified_event

        event_data = {
            "event": {
                "name": base_event.name,
                "location": base_event.location,
                "start_date": base_event.start_date.isoformat() if hasattr(base_event.start_date, 'isoformat') else str(base_event.start_date),
                "end_date": base_event.end_date.isoformat() if base_event.end_date and hasattr(base_event.end_date, 'isoformat') else str(base_event.end_date) if base_event.end_date else None,
                "description": base_event.description,
                "url": base_event.url
            },
            "classification": None,
            "notifications": []
        }

        # Add classification if it's a ClassifiedEvent
        if hasattr(classified_event, 'size') and hasattr(classified_event, 'importance'):
            event_data["classification"] = {
                "size": classified_event.size.value if hasattr(classified_event.size, 'value') else str(classified_event.size),
                "importance": classified_event.importance.value if hasattr(classified_event.importance, 'value') else str(classified_event.importance),
                "target_audiences": [
                    audience.model_dump() if hasattr(audience, 'model_dump') else str(audience)
                    for audience in (classified_event.target_audiences or [])
                ]
            }

        # Add notifications
        event_data["notifications"] = [
            {
                "text": notif.text,
                "language": notif.language,
                "audience": notif.audience.model_dump() if hasattr(notif.audience, 'model_dump') else str(notif.audience)
            } for notif in (notification.notifications or [])
        ]

        notifications_data.append(event_data)

    import json
    return json.dumps({"notifications": notifications_data}, indent=2)

