import sys
import json
import asyncio
import logging
from typing import Dict, List, Any, Optional, Union, Literal
from dotenv import load_dotenv

import click
from mcp.server.lowlevel import Server
from mcp import types
from mcp.server.transport.sse import create_sse_server
from mcp.server.transport.stdio import create_stdio_server

from ticktick.oauth2 import OAuth2
from ticktick.api import TickTickClient

# Configure logging
logging.basicConfig(level=logging.INFO, format="%(asctime)s - %(levelname)s - %(message)s")
logger = logging.getLogger(__name__)

# Load environment variables
load_dotenv()

class TickTickAPI:
    """TickTick API wrapper."""

    def __init__(self, access_token: str, client_id: Optional[str] = None, client_secret: Optional[str] = None):
        """Initialize the TickTick client.

        Args:
            access_token: TickTick OAuth access token
            client_id: TickTick OAuth client ID (optional)
            client_secret: TickTick OAuth client secret (optional)
        """
        self.auth = OAuth2(client_id=client_id, client_secret=client_secret, access_token=access_token)
        self.client = TickTickClient(self.auth)
        logger.info("TickTick client initialized")

    def get_tasks(self) -> List[Dict[str, Any]]:
        """Get all tasks from TickTick."""
        tasks = self.client.get_tasks()
        return [task.to_dict() for task in tasks]

    def get_tags(self) -> List[Dict[str, Any]]:
        """Get all tags from TickTick."""
        tags = self.client.get_tags()
        return [tag.to_dict() for tag in tags]

    def create_task(self, title: str, content: Optional[str] = None, 
                  project_id: Optional[str] = None, tags: Optional[List[str]] = None) -> Dict[str, Any]:
        """Create a new task in TickTick."""
        task = self.client.task.create(title, content)

        if project_id:
            task.project_id = project_id

        if tags:
            task.tags = tags

        updated_task = self.client.task.update(task)
        return updated_task.to_dict()

    def list_projects(self) -> List[Dict[str, Any]]:
        """Get all projects from TickTick."""
        projects = self.client.get_projects()
        return [project.to_dict() for project in projects]


def create_app(api: TickTickAPI) -> Server:
    """Create an MCP server application with TickTick tools.

    Args:
        api: TickTick API instance

    Returns:
        MCP server application
    """
    app = Server("TickTick")

    @app.list_tools()
    async def list_tools() -> types.ToolList:
        """List all available tools."""
        return types.ToolList(
            tools=[
                types.Tool(
                    name="get_tasks",
                    description="Get all tasks from TickTick",
                    input_schema={
                        "type": "object",
                        "properties": {},
                        "required": [],
                    },
                ),
                types.Tool(
                    name="get_tags",
                    description="Get all tags from TickTick",
                    input_schema={
                        "type": "object",
                        "properties": {},
                        "required": [],
                    },
                ),
                types.Tool(
                    name="create_task",
                    description="Create a new task in TickTick",
                    input_schema={
                        "type": "object",
                        "properties": {
                            "title": {"type": "string"},
                            "content": {"type": "string"},
                            "project_id": {"type": "string"},
                            "tags": {"type": "array", "items": {"type": "string"}},
                        },
                        "required": ["title"],
                    },
                ),
                types.Tool(
                    name="list_projects",
                    description="Get all projects from TickTick",
                    input_schema={
                        "type": "object",
                        "properties": {},
                        "required": [],
                    },
                ),
            ]
        )

    @app.call_tool()
    async def call_tool(tool_call: types.ToolCall) -> types.ToolResult:
        """Call a TickTick tool."""
        try:
            if tool_call.name == "get_tasks":
                tasks = api.get_tasks()
                return types.ToolResult(
                    content=types.TextContent(
                        value=json.dumps(tasks, indent=2),
                        mime_type="application/json",
                    )
                )

            elif tool_call.name == "get_tags":
                tags = api.get_tags()
                return types.ToolResult(
                    content=types.TextContent(
                        value=json.dumps(tags, indent=2),
                        mime_type="application/json",
                    )
                )

            elif tool_call.name == "create_task":
                input_data = json.loads(tool_call.input)
                title = input_data.get("title")

                if not title:
                    return types.ToolResult(
                        error=types.Error(
                            code="invalid_input",
                            message="Task title is required",
                        )
                    )

                content = input_data.get("content")
                project_id = input_data.get("project_id")
                tags = input_data.get("tags")

                task = api.create_task(title, content, project_id, tags)

                return types.ToolResult(
                    content=types.TextContent(
                        value=json.dumps(task, indent=2),
                        mime_type="application/json",
                    )
                )

            elif tool_call.name == "list_projects":
                projects = api.list_projects()
                return types.ToolResult(
                    content=types.TextContent(
                        value=json.dumps(projects, indent=2),
                        mime_type="application/json",
                    )
                )

            else:
                return types.ToolResult(
                    error=types.Error(
                        code="unknown_tool",
                        message=f"Unknown tool: {tool_call.name}",
                    )
                )

        except Exception as e:
            logger.exception(f"Error calling tool {tool_call.name}")
            return types.ToolResult(
                error=types.Error(
                    code="internal_error",
                    message=str(e),
                )
            )

    return app


@click.command()
@click.option(
    "--transport",
    type=click.Choice(["stdio", "sse"]),
    default=os.getenv("TRANSPORT_MODE", "stdio"),
    help="Transport mode (stdio or sse)",
)
@click.option(
    "--port",
    type=int,
    default=int(os.getenv("SERVER_PORT", "8000")),
    help="Port to run the server on (SSE mode only)",
)
@click.option(
    "--host",
    type=str,
    default=os.getenv("SERVER_HOST", "0.0.0.0"),
    help="Host to bind to (SSE mode only)",
)
@click.option(
    "--access-token",
    type=str,
    default=os.getenv("TICKTICK_ACCESS_TOKEN"),
    help="TickTick OAuth access token",
)
@click.option(
    "--client-id",
    type=str,
    default=os.getenv("TICKTICK_CLIENT_ID"),
    help="TickTick OAuth client ID",
)
@click.option(
    "--client-secret",
    type=str,
    default=os.getenv("TICKTICK_CLIENT_SECRET"),
    help="TickTick OAuth client secret",
)
@click.option(
    "--debug/--no-debug",
    default=False,
    help="Enable debug logging",
)
def run_server(
    transport: str,
    port: int,
    host: str,
    access_token: Optional[str],
    client_id: Optional[str],
    client_secret: Optional[str],
    debug: bool,
) -> None:
    """Run the TickTick MCP server."""
    # Configure logging level
    if debug:
        logging.getLogger().setLevel(logging.DEBUG)

    # Validate required arguments
    if not access_token:
        logger.error("TickTick access token is required")
        sys.exit(1)

    # Initialize TickTick API
    api = TickTickAPI(
        access_token=access_token,
        client_id=client_id,
        client_secret=client_secret,
    )

    # Create MCP application
    app = create_app(api)

    # Run with appropriate transport
    if transport == "stdio":
        logger.info("Starting TickTick MCP server with stdio transport")
        transport_server = create_stdio_server(app)
    else:  # sse
        logger.info(f"Starting TickTick MCP server with SSE transport on {host}:{port}")
        transport_server = create_sse_server(app, host=host, port=port)

    # Run the server
    asyncio.run(transport_server.serve())


if __name__ == "__main__":
    run_server()

